    private void NIMKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_NIMKeyPressed
        if(evt.getKeyCode() == evt.VK_ENTER)
        {   
            if(NIM.getText().length()==0)
            {
                JOptionPane.showMessageDialog(null, "Data Tidak Boleh Kosong","Konfirmasi", JOptionPane.ERROR_MESSAGE);
                NIM.requestFocus();
                return;
            }
            Simpan.requestFocus();
        }
    }//GEN-LAST:event_NIMKeyPressed

